mapper.select:
select order_id, `status`, a.spec, CONCAT(a.time,d.lease_unit) as time, a.number as num, amount as price, create_date as createTime, c.fm_title as farmName, c.fm_img as farmImg, a.valid_date as beginTime, a.no_valid_date as endTime from block_order as a join farm_manager as b on a.farm_manager_id = b.fm_id join farm as c on b.fm_id = c.fm_id join block_rule d on a.batch_no = d.batch_no where user_id = #{userId}
and a.status = #{status}
and a.no_valid_date < NOW() and a.status = 1
and a.no_valid_date > NOW() and a.status in (1, 3)
ORDER BY createTime desc

select order_id, valid_date, no_valid_date, a.spec, a.number, a.type, a.grade, a.`comment`, a.`status`,
CONCAT(a.time,c.lease_unit) as lease,
b.user_name, b.user_phone from block_order a
join user b on a.user_id = b.id
join block_rule c on a.batch_no = c.batch_no
where a.farm_manager_id = #{farmManagerId}

    and a.status = #{status}


    and a.no_valid_date < NOW()


    and b.user_phone = #{userPhone}


    and a.order_id = #{orderId}


    and a.no_valid_date > NOW()

order by a.create_date desc
limit #{startIndex}, 5

select count(a.order_id) as total from block_order a
join user b on a.user_id = b.id
join block_rule c on a.batch_no = c.batch_no
where a.farm_manager_id = #{farmManagerId}

    and a.status = #{status}


    and a.no_valid_date < NOW()


    and b.user_phone = #{userPhone}


    and a.order_id = #{orderId}


    and a.no_valid_date > NOW()

order by a.create_date desc
