mapper.select:
select * from block_rule where farm_id = #{farmId}

    and batch_no = #{batchNo}


    and type = #{type}


    and validity_begin >= #{validityBegin}


    and #{validityEnd} >= validity_end

limit #{startIndex}, 5

select count(*) as total from block_rule where farm_id = #{farmId}

    and batch_no = #{batchNo}


    and type = #{type}


    and validity_begin >= #{validityBegin}


    and #{validityEnd} >= validity_end
